    public int checkIfHighScore()
    {
        for(int i = 0; i < pastGames.size(); i++)
        {
            if(max<pastGames.get(i)) max = pastGames.get(i);
        }
        return max;
    }
    public void ifHighScore(int a)
    {
        checkIfHighScore();
        if(a==max)
        {
            Winner.setVisible(true);
            Winner1.setVisible(true);
            Winner1.append(max + " is your new high score!");
        }
        else
        {
            Winner.setVisible(true);
            Winner1.setVisible(true);
            Winner1.append(max + " is still your high score.");
        }
    }
