    private void RollActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RollActionPerformed
        if(numTimesRollPressed<3)
        {
            RollsLeft.setText("");
            RollsLeft.setText("Rolls Left: " + (2-numTimesRollPressed));
        }
        if(onStartupCheck == 0)
        {
            onStartUp();
            onStartupCheck++;
        }
        numTimesRollPressed++;
        if(numTimesRollPressed > 3)
        {
            Roll.setBackground(new java.awt.Color(0, 0, 0));
        }
        else
        {
            randomNumberMaker();
        
            if(firstDie == 0) first = firstDie1;
            if(secondDie == 0) second = secondDie1;
            if(thirdDie == 0) third = thirdDie1;
            if(fourthDie == 0) fourth = fourthDie1;
            if(fifthDie == 0) fifth = fifthDie1;
        
            addToArray();
            setText();
            setVisibleFalse();
            setVisibleTrue();
            countEverything();
            enableAllButtonsInTable();
            enableAllDieButtons();
            disableButtonsUsed();
        }
        if(a.checkIfYahtzee() && a.yahtzee > 0)
        {
            YahtzeeYou.setEnabled(true);
        }
    }//GEN-LAST:event_RollActionPerformed

